components.css:
.tsv-icon-contact-status-favorite, .tsv-icon-chat-room-status-favorite {
 z-index: 10;
 left: 13px !important;
 top: 4px !important;
 stroke: rgba(0,0,0,.7);
 stroke-width: 1.3px;
 transform: scale(.61);
}

.tsv-view-banner :is(.tsv-icon-contact-status-favorite, .tsv-icon-chat-room-status-favorite) {
 transform: scale(.4);
 left: 30px !important;
 top: 15px !important;
}

.tsv-icon-contact-status-favorite rect, .tsv-icon-chat-room-status-favorite rect {
 display: none;
}

.tsv-icon-contact-status-favorite path, .tsv-icon-chat-room-status-favorite path {
 /* d: path('M19.4,13.5 21.4,17.6 25.9,18.2 22.7,21.4 23.4,25.9 19.4,23.8 15.4,25.9 16.2,21.4 12.9,18.2 17.4,17.6z') !important; */
 d: path('m 11.566084,-4e-7 3.002256,8.02905 8.563828,0.3742 L 16.423837,13.73968 18.714317,22 11.566084,17.26904 4.4178507,22 6.7083312,13.73968 0,8.4032496 l 8.5638277,-0.3742 z') !important;
 fill: orange;
}

.ts-server-tree-item-background-image img, .ts-server-tree-item-background-image-full img {
 left: 50% !important;
 transform: translate(-50%, 0);
 -webkit-mask-image: linear-gradient(90deg, transparent 0px, #000 var(--tsv-padding-h-large), #000 calc(100% - var(--tsv-padding-h-large)), transparent);
}

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=contacts]) + .tsv-resize-area .tsv-resize-area-container {
 display: none;
} 

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=contacts]) + .tsv-resize-area:after {
 width: 100%;
 display: block;
 content: "You shall not be lured,\A By humans or things!\A You shall not be fooled,\A By what the world brings!" attr(title);
 padding: 5px 15px;
 white-space: pre-wrap;
}

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=bookmarks]) + .tsv-resize-area .tsv-resize-area-container {
 display: none;
} 

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=bookmarks]) + .tsv-resize-area:after {
 width: 100%;
 display: block;
 content: "You shall not hear,\A You shall not speak,\A You shall not see,\A You shall not seek!" attr(title);
 padding: 5px 15px;
 white-space: pre-wrap;
}

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=groups]) + .tsv-resize-area .tsv-resize-area-container {
 display: none;
} 

.tsv-tabs:has(.tsv-bar-item.tsv-tab-item-active svg[name=groups]) + .tsv-resize-area:after {
 width: 100%;
 display: block;
 content: "You shall not be moved,\A By the voices of the crowd!\A You shall not be lost,\A In the chaos of the loud!" attr(title);
 padding: 5px 15px;
 white-space: pre-wrap;
}

.tsv-activity-group-list > li.tsv-item .tsv-text-truncate,
.tsv-activity-group-list > li li:first-child .tsv-text-truncate,
.tsv-header-bar-below-tools:has(svg[name=bookmark]) .tsv-text-truncate,
.tsv-server-info-footer p:first-child,
.tsv-header-floating:has(+* .tsv-server-info-footer) .tsv-text-truncate,
.tsv-dashboard-widget-content:has(.tsv-icon-server-connect) .tsv-text-truncate,
.ts-sidebar-overlay-header-channel-servername {
 filter: blur(6px);
 transition: .3s ease all;
}

.tsv-activity-group-list > li.tsv-item .tsv-text-truncate:hover,
.tsv-activity-group-list > li li:first-child .tsv-text-truncate:hover,
.tsv-header-bar-below-tools:has(svg[name=bookmark]) .tsv-text-truncate:hover,
.tsv-server-info-footer p:first-child:hover,
.tsv-header-floating:has(+* .tsv-server-info-footer) .tsv-text-truncate:hover,
.tsv-dashboard-widget-content:has(.tsv-icon-server-connect) .tsv-text-truncate:hover,
.ts-sidebar-overlay-header-channel-servername:hover {
 filter: blur(0px);
}

.tsv-dashboard-widget-content:has(.tsv-changelog-selectable) {
 filter: blur(6px);
 transition: .3s ease all;
}

.tsv-dashboard-widget-content:has(.tsv-changelog-selectable):hover {
 filter: blur(0px);
}
